mapper.select:
select s.score_id,s.stu_id,stu.stu_name,e.exam_name,c.class_name,s.language_score,s.math_score,s.english_score,s.politics_score,s.history_score,s.geog_score,SUM(s.language_score+s.math_score+s.english_score+s.politics_score+s.history_score+s.geog_score)total_points from t_score s,t_exam e,t_student stu,t_class c where s.stu_id=stu.stu_id AND s.class_id=c.class_id and s.exam_id=#{examId} and s.exam_id=e.exam_id group by s.score_id
order by ${prop} ${order}
order by s.score_id ASC
limit #{currentPage},#{pageSize}

select COUNT(*)
from t_score
where exam_id = #{examId}

select count(*)
from t_score s
where s.class_id = #{classId}

select count(*)
from t_score s
where s.class_id = #{classId}
  and s.exam_id = #{examId}

select t.class_id
from t_score t
where t.score_id = #{scoerId}

select t.exam_id
from t_score t
where t.score_id = #{scoerId}
